from Tkinter import * from tkMessageBox import *
class EntryDemo( Frame ): def __init__( self ): Frame.__init__( self ) self.pack( expand = YES, fill = BOTH ) self.master.title( "Test datamodtagere" ) self.master.geometry( "325x100" )
self.frame1 = Frame( self ) self.frame1.pack( pady = 5 ) # text1 oprettes som barnebarn af Frame # entry er endnu en underklasse i Widget klassen (ls barn af) self.text1 = Entry( self.frame1, name = "tekst1" ) self.text1.bind( "<Return>", self.visIndhold ) self.text1.pack( side = LEFT, padx = 5 )
self.text2 = Entry( self.frame1, name = "tekst2" ) self.text2.insert( INSERT, "Skriv tekst her" ) self.text2.bind( "<Return>", self.visIndhold ) self.text2.pack( side = LEFT, padx = 5 )
self.frame2 = Frame( self ) self.frame2.pack( pady = 5 )
self.text3 = Entry( self.frame2, name = "tekst3" ) self.text3.insert( INSERT, "Ikke editerbar tekstboks" ) self.text3.config( state = DISABLED ) self.text3.bind( "<Return>", self.visIndhold ) self.text3.pack( side = LEFT, padx = 5 )
self.text4 = Entry( self.frame2, name = "tekst4",show = "*" ) self.text4.insert( INSERT, "Hemmelig tekst" ) self.text4.bind( "<Return>", self.visIndhold ) self.text4.pack( side = LEFT, padx = 5 )
def visIndhold( self, event ): navn = event.widget.winfo_name() indhold = event.widget.get() showinfo( "Melding", navn + ": " + indhold )
def main(): EntryDemo().mainloop()
if __name__ == "__main__":main()

