from Tkinter import *
class Arving(Frame): # Klassehovede Arving er barn af Frame def __init__(Arving, mor=None): Frame.__init__(Arving, mor) # initialiser superklasse (superclass) Arving.pack() Arving.data = 10 Arving.opret_arvinger() # Forener barn med mor
def opret_arvinger(Arving): widget = Button(Arving, text='Knaptekst!', command=Arving.message) widget.pack(side=LEFT)
def message(Arving): Arving.data = Arving.data + 1 print 'Sum %s' % Arving.data # udskriver på skal (kunne være til fil)
if __name__ == '__main__': Arving().mainloop()

