from Tkinter import * class PopupMenuDemo( Frame ): def __init__( self ): Frame.__init__( self ) self.pack( expand = YES, fill = BOTH ) self.master.title( "Popup menu demo" ) self.master.geometry( "350x250" ) self.frame = Frame( self, bg = "green" ) self.frame.pack( expand= YES, fill = BOTH )
self.menu = Menu( self.frame, tearoff = 0 ) # De engelske farvenavne her bruges til farveskifte farver = [ "yellow", "green", "red", "blue", "grey" ] self.selectedColor = StringVar() self.selectedColor.set( farver[ 0 ] )
for item in farver: self.menu.add_radiobutton( label = item, variable = self.selectedColor, command = self.skiftBaggrundsfarve ) # BEMRK her bruges midterste museknap self.frame.bind( "<Button-2>", self.popUpMenu )
def popUpMenu( self, event ): self.menu.post( event.x_root, event.y_root )
def skiftBaggrundsfarve( self ): self.frame.config( bg = self.selectedColor.get() )
def main(): PopupMenuDemo().mainloop()
if __name__ == "__main__": main()

from Tkinter import * class klatmaleren( Frame ):
def __init__( self ): Frame.__init__( self ) self.pack( expand = YES, fill = BOTH ) self.master.title( "Et meget simpelt maleprogram" ) self.master.geometry( "350x250" )
self.message = Label( self, text = "Flyt musen og tegn" ) self.message.pack( side = BOTTOM )
self.canvas = Canvas( self ) self.canvas.pack( expand = YES, fill = BOTH )
# bind flyt mus til Canvas self.canvas.bind( "<B1-Motion>", self.paint )
def paint( self, event ): """Tegn en oval med radius 1 den er stregtykkelsen"""
x1, y1 = ( event.x - 1 ), ( event.y - 1 ) x2, y2 = ( event.x + 1 ), ( event.y + 1 ) self.canvas.create_oval( x1, y1, x2, y2, fill = "red" )
def main(): klatmaleren().mainloop()
if __name__ == "__main__": main()

