from Tkinter import *
class ScaleCanvasDemo( Frame ): def __init__( self ): Frame.__init__( self ) self.pack( expand = YES, fill = BOTH ) self.master.title( "Canvas og Scale widget demo" ) self.master.geometry( "280x350" )
# Opret Scale self.scale = Scale( self, from_ = 0, to = 300, orient = HORIZONTAL, command = self.opdaterCirkel ) self.scale.pack( side = BOTTOM, fill = X ) self.scale.set( 10 )
# opret canvas og tegn cirkel self.canvas = Canvas( self, bg = "green" ) self.canvas.pack( expand = YES, fill = BOTH )
def opdaterCirkel( self, scaleValue ): """Slet cirklen, beregn ny størrelse og gentegn cirkel"""
end = int( scaleValue ) + 10 self.canvas.delete( "circle" ) self.canvas.create_oval( 10, 10, end, end, fill = "blue", tags = "circle" )
def main(): ScaleCanvasDemo().mainloop()
if __name__ == "__main__": main()

from Tkinter import *

class Listeboks(Frame):
    def __init__(self, mor,elementer):
        Frame.__init__(self,mor)
        self.elementer = []
        for element,w in elementer:
            frame = Frame(self); frame.pack(side=LEFT, expand=YES, fill=BOTH)
            Label(frame, text=element, borderwidth=1, relief=RAISED).pack(fill=X)
            label = Listbox(frame, width=w, borderwidth=0, selectborderwidth=0,
                         relief=FLAT, exportselection=FALSE)
            label.pack(expand=YES, fill=BOTH)
            self.elementer.append(label)
            label.bind('<B1-Motion>', lambda e, s=self: s._select(e.y))
            label.bind('<Button-1>', lambda e, s=self: s._select(e.y))
            label.bind('<Leave>', lambda e: 'break')
            label.bind('<B2-Motion>', lambda e, s=self: s._b2motion(e.x, e.y))
            label.bind('<Button-2>', lambda e, s=self: s._button2(e.x, e.y))
        frame = Frame(self); frame.pack(side=LEFT, fill=Y)
        Label(frame, borderwidth=1, relief=RAISED).pack(fill=X)
        rulleskakt = Scrollbar(frame, orient=VERTICAL, command=self._scroll)
        rulleskakt.pack(expand=YES, fill=Y)
        self.elementer[0]['yscrollcommand']=rulleskakt.set

    def _select(self, y):
        row = self.elementer[0].nearest(y)
        self.selection_clear(0, END)
        self.selection_set(row)
        return 'break'

    def _button2(self, x, y):
        for element in self.elementer: element.scan_mark(x, y)
        return 'break'

    def _b2motion(self, x, y):
        for element in self.elementer: element.scan_dragto(x, y)
        return 'break'

    def _scroll(self, *args):
        for element in self.elementer:
            apply(element.yview, args)

    def curselection(self):
        return self.elementer[0].curselection()

    def slet(self, begynd, slut=None):
        for element in self.elementer:
            element.slet(begynd, slut)

    def hent(self, begynd, slut=None):
        result = []
        for element in self.elementer:
            result.append(element.hent(begynd,slut))
        if slut: return apply(map, [None] + result)
        return result

    def indeks(self, indeks):
        self.elementer[0].indeks(indeks)

    def insert(self, indeks, *elements):
        for e in elements:
            i = 0
            for element in self.elementer:
                element.insert(indeks, e[i])
                i = i + 1

    def size(self):
        return self.elementer[0].size()

    def see(self, indeks):
        for element in self.elementer:
            element.see(indeks)

    def selection_anchor(self, indeks):
        for element in self.elementer:
            element.selection_anchor(indeks)

    def selection_clear(self, begynd, slut=None):
        for element in self.elementer:
            element.selection_clear(begynd, slut)

    def selection_includes(self, indeks):
        return self.elementer[0].selection_includes(indeks)

    def selection_set(self, begynd, slut=None):
        for element in self.elementer:
            element.selection_set(begynd, slut)

if __name__ == '__main__':
    win = Tk()
    Label(win, text='Listeboks').pack()
    listeboks = Listeboks(win, (("Overskrift 1", 20), ("Overskrift  2", 10),
    ("Overskrift  3", 10)))

    listeboks.insert(END,("del 1", "del 2", "del 3"))
    listeboks.insert(END,("del 4", "del 5", "del 6"))
    listeboks.insert(END,("del 7", "del 8", "del 9"))
    listeboks.pack(expand=YES,fill=BOTH)
    win.mainloop()

from Tkinter import *
class ScaleCanvasDemo( Frame ): def __init__( self ): Frame.__init__( self ) self.pack( expand = YES, fill = BOTH ) self.master.title( "Scale og Canvas demo" ) self.master.geometry( "300x270" )
self.control = Scale( self, from_ = 0, to = 300, orient = HORIZONTAL, command = self.updateCircle ) self.control.pack( side = BOTTOM, fill = X ) self.control.set( 10 )
self.display = Canvas( self, bg = "navy" ) self.display.pack( expand = YES, fill = BOTH )
def updateCircle( self, scaleValue ): end = int( scaleValue ) + 10 self.display.delete( "circle" ) self.display.create_oval( 10, 10, end, end, fill = "red", tags = "circle" )
def main(): ScaleCanvasDemo().mainloop()
if __name__ == "__main__": main()
