from Tkinter import * from tkMessageBox import askyesno, showerror
class Dialog(Frame): # arver Frame def __init__(self, master=None): #Dialogs konstruktør Frame.__init__(self, master) # Frames konstruktør Pack.config(self) self.opretArvinger()
def Hilsen(self): # arver Dialog print "En hilsen fra Bornholm til dig!" #sendes til skal, men kunne være til fil
def opretArvinger(self): # arver Dialog Label(self, text='Hej! jeg vil gerne i dialog med dig.').pack(side=TOP) Button(self, text='Popup 1', command=self.dialog1).pack() # arver opretArvinger Button(self, text='Popup 2', command=self.dialog2).pack() Button(self, text='Noget til dig', command=self.Hilsen ).pack(side=LEFT) Button(self, text='Afslut', command=self.quit ).pack(side=RIGHT)
def dialog1(self): # arver Dialog svar = askyesno("Vil du se tkMessageBox\ndialogen i funktion?") if svar: self.dialog2()
def dialog2(self): # arver Dialog showerror("BESKYTTET AFSNIT", "\nHer har du vist ingen adgang min ven...")

from Tkinter import *
class Klasse(Frame):                 # Opret subklasse
    def __init__(Klasse, side=TOP):
        Frame.__init__(Klasse)       # Subklassekonstruktor
        Klasse.pack()
        Klasse.make_widgets()        # metodekald
    def make_widgets(Klasse):
        Button(Klasse,
            text="Klik her", command=Klasse.quit).pack(side=LEFT)
if __name__ == '__main__': Klasse().mainloop()

from Tkinter import * class tekstFunktioner(Frame): def __init__(self, parent=None, tekst='', indfil=None): Frame.__init__(self, parent) self.pack(expand=YES, fill=BOTH) self.opretArvinger() self.tekstvalg(tekst, indfil)
def opretArvinger(self): rulleskakt = Scrollbar(self) tekst = Text(self, relief=SUNKEN) rulleskakt.config(command=tekst.yview) # xlink rulleskakt og tekst tekst.config(yscrollcommand=rulleskakt.set) # flyttes en flyttes alle rulleskakt.pack(side=RIGHT, fill=Y) # pack først = clip sidst tekst.pack(side=LEFT, expand=YES, fill=BOTH) # tekst clipped først self.tekst = tekst
def tekstvalg(self, tekst='', indfil=None): if indfil: tekst = open(indfil, 'r').read() self.tekst.delete('1.0', END) # slet hele teksten self.tekst.insert('1.0', tekst) # tekst ind linje 1 pos 0 self.tekst.mark_set(INSERT, '1.0') # set insert cursor self.tekst.focus() # gem brugervalg
def hentTekst(self): # returner streng return self.tekst.get('1.0', END+'-1c') # hele teksten
if __name__ == '__main__': root = Tk() try: streng = tekstFunktioner(indfil=sys.argv[1]) # filnavn på kommandolinje except IndexError: streng = tekstFunktioner(tekst='Tekst\nindsættes her')

Eksemplet viser en udvidet listeboks:
from Tkinter import *
class Listeboks(Frame):
    def __init__(self, mor,elementer):
        Frame.__init__(self,mor)
        self.elementer = []
        for element,w in elementer:
            frame = Frame(self); frame.pack(side=LEFT, expand=YES, fill=BOTH)
            Label(frame, text=element, borderwidth=1, relief=RAISED).pack(fill=X)
            label = Listbox(frame, width=w, borderwidth=0, selectborderwidth=0,
                         relief=FLAT, exportselection=FALSE)
            label.pack(expand=YES, fill=BOTH)
            self.elementer.append(label)
            label.bind('<B1-Motion>', lambda e, s=self: s._select(e.y))
            label.bind('<Button-1>', lambda e, s=self: s._select(e.y))
            label.bind('<Leave>', lambda e: 'break')
            label.bind('<B2-Motion>', lambda e, s=self: s._b2motion(e.x, e.y))
            label.bind('<Button-2>', lambda e, s=self: s._button2(e.x, e.y))
        frame = Frame(self); frame.pack(side=LEFT, fill=Y)
        Label(frame, borderwidth=1, relief=RAISED).pack(fill=X)
        rulleskakt = Scrollbar(frame, orient=VERTICAL, command=self._scroll)
        rulleskakt.pack(expand=YES, fill=Y)
        self.elementer[0]['yscrollcommand']=rulleskakt.set
    def _select(self, y):
        row = self.elementer[0].nearest(y)
        self.selection_clear(0, END)
        self.selection_set(row)
        return 'break'
    def _button2(self, x, y):
        for element in self.elementer: element.scan_mark(x, y)
        return 'break'
    def _b2motion(self, x, y):
        for element in self.elementer: element.scan_dragto(x, y)
        return 'break'
    def _scroll(self, *args):
        for element in self.elementer:
            apply(element.yview, args)
    def curselection(self):
        return self.elementer[0].curselection()
    def slet(self, begynd, slut=None):
        for element in self.elementer:
            element.slet(begynd, slut)
    def hent(self, begynd, slut=None):
        result = []
        for element in self.elementer:
            result.append(element.hent(begynd,slut))
        if slut: return apply(map, [None] + result)
        return result
    def indeks(self, indeks):
        self.elementer[0].indeks(indeks)
    def insert(self, indeks, *elements):
        for e in elements:
            i = 0
            for element in self.elementer:
                element.insert(indeks, e[i])
                i = i + 1
    def size(self):
        return self.elementer[0].size()
    def see(self, indeks):
        for element in self.elementer:
            element.see(indeks)
    def selection_anchor(self, indeks):
        for element in self.elementer:
            element.selection_anchor(indeks)
    def selection_clear(self, begynd, slut=None):
        for element in self.elementer:
            element.selection_clear(begynd, slut)
    def selection_includes(self, indeks):
        return self.elementer[0].selection_includes(indeks)
    def selection_set(self, begynd, slut=None):
        for element in self.elementer:
            element.selection_set(begynd, slut)
if __name__ == '__main__':
    win = Tk()
    Label(win, text='Listeboks').pack()
    listeboks = Listeboks(win, (("Overskrift 1", 20), ("Overskrift  2", 10),
    ("Overskrift  3", 10)))
    listeboks.insert(END,("del 1", "del 2", "del 3"))
    listeboks.insert(END,("del 4", "del 5", "del 6"))
    listeboks.insert(END,("del 7", "del 8", "del 9"))
    listeboks.pack(expand=YES,fill=BOTH)
    win.mainloop()
